mapper.select:
select s.sid,g.gid,s.sname,s.snumber,st.stname,g.gname,g.gnumber,g.gstarttime,g.gendtime,g.gclosedtime,s.pstatus as state
from site as s 
left join sitetype as st on s.stid = st.stid 
left join gymnasiummessage as g on g.gid = st.gid 
left join orders  as o on o.sid = s.sid

	
		and g.gid = #{gid}
	
	
		and s.sname = #{sname}
	
	
		and st.stname = #{stname}
	
	
		and s.pstatus = #{pstatus}
	
	
		
			and o.otime between #{starttime} and #{endtime}
		
	

GROUP BY s.sname,s.snumber
order by s.time desc
limit #{pageNum},#{pageSize}

SELECT *
FROM site AS s 
LEFT JOIN sitetype AS st ON s.stid = st.stid 
LEFT JOIN gymnasiummessage AS g ON g.gid = st.gid 
LEFT JOIN orders  AS o ON o.sid = s.sid

	
		and g.gid = #{gid}
	
	
		and s.sname = #{sname}
	
	
		and st.stname = #{stname}
	
	
		and s.pstatus = #{pstatus}
	
	
		
			and o.otime between #{starttime} and #{endtime}
		
	

GROUP BY sname,s.snumber

select * from site as s left join sitetype as st on s.stid = st.stid left join orders as o on s.sid = o.sid
and year(o.ostarttime) = #{year} and month(o.ostarttime) = #{month} and day(o.ostarttime) = #{day}
order by s.snumber

SELECT * FROM site s
LEFT JOIN sitetype st ON s.stid = st.stid
LEFT JOIN gymnasiummessage g ON g.gid = st.gid

	
		and snumber = #{snumber}
	
	
		and g.gid = #{gid}
	
	
		and s.sid = #{sid}
	
	
		 and st.stname = #{stname}

SELECT * FROM site s
LEFT JOIN sitetype st ON s.stid = st.stid
LEFT JOIN gymnasiummessage g ON g.gid = st.gid

	
		and snumber = #{snumber}
	
	
		and g.gid = #{gid}
	
	
		and s.sid = #{sid}
	
	
		 and st.stname = #{stname}
	

group by s.sname

select g.gid,s.sname,s.snumber,g.gname,st.stname,count(o.sid) as count,s.pstatus,g.gstarttime,g.gendtime,g.gclosedtime from site s left join orders o on s.sid = o.sid left join sitetype st on st.stid = s.stid left join gymnasiummessage g on g.gid = st.gid
and st.stname = #{stname}
and o.ostarttime between #{starttime} and #{endtime}
group by s.sid

SELECT * FROM site s
LEFT JOIN sitetype st ON s.stid = st.stid

	
		and sname not in 
		
			#{item.sname}
		
	
	
		stname = #{stname}
